SampleCode.Old:

#include "itkRealTimeClock.h"
itk::RealTimeClock::Pointer clock = itk::RealTimeClock::New();
double timeStart = clock->GetTimeStamp();

SampleCode.New:

#include "itkRealTimeClock.h"
itk::RealTimeClock::Pointer clock = itk::RealTimeClock::New();
itk::RealTimeClock::TimeStampType timeStart = clock->GetTimeInSeconds();
